mapper.select:
SELECT COUNT(*)
FROM TB_PRODUCT_LIKE
WHERE PRODUCT_CODE = #{productCode}

SELECT PRODUCT_CODE
FROM TB_PRODUCT_LIKE
WHERE USER_CODE = #{userCode}

    AND PRODUCT_CODE <= #{maxProductCode}

LIMIT #{pageSize} OFFSET #{offset}

mapper.delete:
DELETE FROM TB_PRODUCT_LIKE
WHERE USER_CODE = #{userCode}
  AND PRODUCT_CODE = #{productCode}
