mapper.select:
SELECT CEIL (count(*) / #{pageSize}), MAX(PRODUCT_CODE)
FROM TB_PRODUCT
WHERE USER_CODE = #{userCode}

SELECT p.PRODUCT_CODE, CONCAT(tl.LOCATION_SI_NM, ' ' , tl.LOCATION_GU_NM, ' ' , tl.LOCATION_DONG_NM, ' ', tl.LOCATION_DONG_SUB_NM),
p.CATEGORY,  p.TITLE, p.THUMBNAIL, p.PRODUCT_STATUS, p.MCOIN, p.REQ_CNT, p.INSERT_DATE, p.UPDATE_DATE
FROM TB_PRODUCT p
JOIN TB_LOCATION_TEMP tl ON tl.LOCATION_DONG_CD = p.LOCATION_DONG_CD
WHERE 1=1

    AND PRODUCT_CODE <= #{maxProductCode}


    AND p.LOCATION_DONG_CD LIKE CONCAT(#{locationCode}, '%')


    AND p.LOCATION_DONG_CD = #{locationCode}


    AND p.CATEGORY = #{category}


    AND p.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')


    AND p.PRODUCT_STATUS = #{productStatus}


    ORDER BY p.UPDATE_DATE DESC, p.MCOIN ${mcoinSort ? 'ASC' : 'DESC'}


    ORDER BY p.UPDATE_DATE DESC

LIMIT #{pageSize} OFFSET #{offset}

SELECT PRODUCT_CODE, USER_CODE, LOCATION_DONG_CD, CATEGORY, TITLE, THUMBNAIL, PRODUCT_STATUS, MCOIN, REQ_CNT, INSERT_DATE, UPDATE_DATE
FROM TB_PRODUCT
WHERE PRODUCT_CODE = #{productCode}

SELECT PRODUCT_CODE, USER_CODE, LOCATION_DONG_CD, CATEGORY, TITLE, THUMBNAIL, PRODUCT_STATUS, MCOIN, REQ_CNT, INSERT_DATE, UPDATE_DATE
FROM TB_PRODUCT
WHERE USER_CODE = #{userCode}
LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE TB_PRODUCT
SET REQ_CNT = #{reqCnt}
WHERE PRODUCT_CODE = #{productCode}

UPDATE TB_PRODUCT_DETAIL
SET LIKES = #{likes}
WHERE PRODUCT_CODE = #{productCode}
